from typing import Counter, List

class Solution:
    def minimumOperationsToWriteY(self, grid: List[List[int]]) -> int:
        n = len(grid)
        y_counter = Counter({0: 0, 1: 0, 2: 0})
        rest_counter = Counter({0: 0, 1: 0, 2: 0})
        for i, row in enumerate(grid):
            for j, cell in enumerate(row):
                if ((j == i or j == n - i - 1) and i <= n // 2) or (i > n // 2 and j == n // 2):
                    y_counter[cell] += 1
                else:
                    rest_counter[cell] += 1

        max_ele = 0
        for i in y_counter.keys():
            for j in rest_counter.keys():
                if i != j:
                    max_ele = max(max_ele, y_counter[i] + rest_counter[j])
        
        res = len(grid) ** 2 - max_ele
        return res
    
def test1():
    solution = Solution()
    grid = [[1,2,2],[1,1,0],[0,1,0]]

    assert solution.minimumOperationsToWriteY(grid) == 3

def test2():
    solution = Solution()
    grid = [[0,1,0,1,0],[2,1,0,1,2],[2,2,2,0,1],[2,2,2,2,2],[2,1,2,2,2]]

    assert solution.minimumOperationsToWriteY(grid) == 12

